<template>
  <div class="timeline-view">
    <header-full :commonData='commonData' :section='sectionName' 
                      v-on:openSearchBar="openSearchBar" v-on:openSideBar="openSideBar"/>
    <spinner :show="loading"></spinner>
    <section :style="{ position: 'relative' }">
      <div class="titleBox"/>
      <figure class="post-image" :style="{ background: 'url(//storage.googleapis.com/mirrormedia-files/assets/images/20170109160902-b20ee536a0e884d73159d4846df1d33d.jpg) center center / cover no-repeat' }"></figure>
    </section>

    <div class="timelineWrapper">
      <section class="tweet" v-for="item in rep">
        <div class="description">
          <div class="datetime" v-text="dateformat(item.created_at, 'mm/dd HH:MM')"></div>
          <div class="content" v-html="twitterAutoLink(item.text, item.entities.urls)"></div>
          <div class="share"></div>
        </div>
        <div class="box topLine">
          <div class="clock"></div>
          <div class="innerBox leftLine">
            <div class="heroImg">
              <a :href="`https://twitter.com/MirrorWatchTW/status/${getValue(item, [ 'id_str' ], '')}`">
                <img :src="getValue(item, [ 'extended_entities', 'media', 0, 'media_url_https' ], '/public/notImage.png')" 
                    v-if="getValue(item, [ 'extended_entities', 'media', 0, 'media_url_https' ])"  />
              </a>
            </div>

          </div>
        </div>
      </section>
    </div>
    <div class="container" :style="{ marginBottom: '20px' }">
      <more v-on:loadMore="loadMore" />
    </div>
    <footer-full :commonData='commonData' :section='sectionName' />
    <search-full v-show='openSearch' v-on:closeSearchBar="closeSearchBar" />
    <sidebar-full :section='sectionName' :sections='commonData.sections' 
                      :openSide='openSide' v-on:closeSideBar="closeSideBar" />
  </div>
</template>

<script>
import { getValue } from '../utils/comm'
import _ from 'lodash'
import FooterFull from '../components/FooterFull.vue'
import HeaderFull from '../components/HeaderFull.vue'
import More from '../components/More.vue'
import SearchFull from '../components/SearchFull.vue'
import SidebarFull from '../components/SidebarFull.vue'
import Spinner from '../components/Spinner.vue'
import dateformat from 'dateformat'
import twitter from 'twitter-text'


const fetchCommonData = (store) => {
  return store.dispatch('FETCH_COMMONDATA', { 'endpoints': [ 'sectionfeatured' ] })
}

const fetchData = (store) => {
  return fetchCommonData(store)
}

function fetchTwitter (url) {
  const superagent = require('superagent')
  let apiHost = '/api'
  return new Promise(resolve => {
    superagent
    .get(apiHost + url)
    .end((err, response) => {
      if (!err && response) {
        resolve(JSON.parse(response.text))
      } else {
        resolve('{\'error\':' + err + '}')
      }
    })
  })
}

export default {
  name: 'timeline-view',
  data () {
    return {
      commonData: this.$store.state.commonData,
      loading: true,
      openSearch: false,
      openSide: false,
      rep: '',
      sectionName: this.$store.state.route.params.title
    }
  },
  components: { 
    'footer-full': FooterFull,
    'header-full': HeaderFull,
    'more': More,
    'search-full': SearchFull,
    'sidebar-full': SidebarFull,
    Spinner
  },
  computed: {
    lastItemId() {
      return _.get(_.last(this.rep), 'id', 0)
    },
  },
  metaInfo () {
    const title = 'Timeline :: ' + this.$route.params.title.toUpperCase()
    return {
      title,
      meta: [{ vmid: 'description', name: 'description', content: title }]
    }
  },
  methods: {
    closeSearchBar () {
      this.openSearch = false
    },
    closeSideBar () {
      this.openSide = false
    },
    dateformat,
    getValue,
    loadMore() {
      fetchTwitter(`/twitter?screen_name=MirrorWatchTW&count=10&max_id=${this.lastItemId}`).then(
        response => {
          if(response.length > 0 && !response[0][ 'code' ]) {
            this.rep = this.rep.concat(response)
          }
          this.loading = false
        },
        error => {
          console.error('Failed!', error) //eslint-disable-line
        }
      )
    },
    openSearchBar () {
      this.openSearch = true
    },
    openSideBar () {
      this.openSide = true
    },
    twitterAutoLink: twitter.autoLink,
  },
  preFetch: fetchData,
  beforeMount () {
    fetchTwitter(`/twitter?screen_name=MirrorWatchTW&count=10`).then(
      response => {
        if(response.length > 0 && !response[0][ 'code' ]) {
          this.rep = response
        }
        this.loading = false
      },
      error => {
        console.error('Failed!', error) //eslint-disable-line
      }
    )
  }
}
</script>

